mapper.select:
SELECT



FROM jiadian  a



        and a.id in

        #{item}



        and a.jiadian_name like CONCAT('%',#{params.jiadianName},'%')


        and a.jiadian_uuid_number like CONCAT('%',#{params.jiadianUuidNumber},'%')


        and a.jiadian_types = #{params.jiadianTypes}


          and a.jiadian_kucun_number >= #{params.jiadianKucunNumberStart}   


          and a.jiadian_kucun_number <= #{params.jiadianKucunNumberEnd}   


        and a.jiadian_kucun_number = #{params.jiadianKucunNumber}


          and a.jiadian_old_money >= #{params.jiadianOldMoneyStart}   


          and a.jiadian_old_money <= #{params.jiadianOldMoneyEnd}   


          and a.jiadian_new_money >= #{params.jiadianNewMoneyStart}   


          and a.jiadian_new_money <= #{params.jiadianNewMoneyEnd}   


          and a.jiadian_clicknum >= #{params.jiadianClicknumStart}   


          and a.jiadian_clicknum <= #{params.jiadianClicknumEnd}   


        and a.jiadian_clicknum = #{params.jiadianClicknum}


        and a.jiadian_content like CONCAT('%',#{params.jiadianContent},'%')


        and a.shangxia_types = #{params.shangxiaTypes}


          and a.jiadian_delete >= #{params.jiadianDeleteStart}   


          and a.jiadian_delete <= #{params.jiadianDeleteEnd}   


        and a.jiadian_delete = #{params.jiadianDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
