mapper.select:
SELECT


,address.yonghu_id as addressYonghuId
,address.address_name as addressName
,address.address_phone as addressPhone
,address.address_dizhi as addressDizhi
,address.isdefault_types as isdefaultTypes
,jiadian.jiadian_name as jiadianName
,jiadian.jiadian_uuid_number as jiadianUuidNumber
,jiadian.jiadian_photo as jiadianPhoto
,jiadian.jiadian_types as jiadianTypes
,jiadian.jiadian_kucun_number as jiadianKucunNumber
,jiadian.jiadian_old_money as jiadianOldMoney
,jiadian.jiadian_new_money as jiadianNewMoney
,jiadian.jiadian_clicknum as jiadianClicknum
,jiadian.jiadian_content as jiadianContent
,jiadian.shangxia_types as shangxiaTypes
,jiadian.jiadian_delete as jiadianDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney

FROM jiadian_order  a
left JOIN address address ON a.address_id = address.id
left JOIN jiadian jiadian ON a.jiadian_id = jiadian.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and a.jiadian_order_uuid_number like CONCAT('%',#{params.jiadianOrderUuidNumber},'%')


        and (
            a.address_id = #{params.addressId}
        )


        and (
            a.jiadian_id = #{params.jiadianId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


          and a.buy_number >= #{params.buyNumberStart}   


          and a.buy_number <= #{params.buyNumberEnd}   


        and a.buy_number = #{params.buyNumber}


          and a.jiadian_order_true_price >= #{params.jiadianOrderTruePriceStart}   


          and a.jiadian_order_true_price <= #{params.jiadianOrderTruePriceEnd}   


        and a.jiadian_order_courier_name like CONCAT('%',#{params.jiadianOrderCourierName},'%')


        and a.jiadian_order_courier_number like CONCAT('%',#{params.jiadianOrderCourierNumber},'%')


        and a.jiadian_order_types = #{params.jiadianOrderTypes}


        and a.jiadian_order_payment_types = #{params.jiadianOrderPaymentTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.address_id IS NOT NULL


        and address.address_name like CONCAT('%',#{params.addressName},'%')


        and address.address_phone like CONCAT('%',#{params.addressPhone},'%')


        and address.address_dizhi like CONCAT('%',#{params.addressDizhi},'%')


        and address.isdefault_types = #{params.isdefaultTypes}



          and UNIX_TIMESTAMP(address.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(address.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


          and UNIX_TIMESTAMP(address.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(address.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 



        and a.jiadian_id IS NOT NULL


        and jiadian.jiadian_name like CONCAT('%',#{params.jiadianName},'%')


        and jiadian.jiadian_uuid_number like CONCAT('%',#{params.jiadianUuidNumber},'%')


        and jiadian.jiadian_types = #{params.jiadianTypes}



          and jiadian.jiadian_kucun_number >= #{params.jiadianKucunNumberStart}   


          and jiadian.jiadian_kucun_number <= #{params.jiadianKucunNumberEnd}   


        and jiadian.jiadian_kucun_number = #{params.jiadianKucunNumber}


          and jiadian.jiadian_old_money >= #{params.jiadianOldMoneyStart}   


          and jiadian.jiadian_old_money <= #{params.jiadianOldMoneyEnd}   


          and jiadian.jiadian_new_money >= #{params.jiadianNewMoneyStart}   


          and jiadian.jiadian_new_money <= #{params.jiadianNewMoneyEnd}   


          and jiadian.jiadian_clicknum >= #{params.jiadianClicknumStart}   


          and jiadian.jiadian_clicknum <= #{params.jiadianClicknumEnd}   


        and jiadian.jiadian_clicknum = #{params.jiadianClicknum}


        and jiadian.jiadian_content like CONCAT('%',#{params.jiadianContent},'%')


        and jiadian.shangxia_types = #{params.shangxiaTypes}



          and jiadian.jiadian_delete >= #{params.jiadianDeleteStart}   


          and jiadian.jiadian_delete <= #{params.jiadianDeleteEnd}   


        and jiadian.jiadian_delete = #{params.jiadianDelete}


          and UNIX_TIMESTAMP(jiadian.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(jiadian.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


order by a.${params.sort} ${params.order}
